// frontend/e2e/cards/responsive-behavior.spec.ts
import { test, expect } from '@playwright/test'

test.describe('Card Responsive Behavior', () => {
  test('shows compact variant on mobile', async ({ page }) => {
    await page.setViewportSize({ width: 375, height: 667 }) // iPhone SE
    await page.goto('/agents')

    // Compact card visible
    const compactCard = page.locator('.sm\\:hidden').first()
    await expect(compactCard).toBeVisible()

    // Default card hidden
    const defaultCard = page.locator('.hidden.sm\\:block').first()
    await expect(defaultCard).not.toBeVisible()
  })

  test('shows default variant on desktop', async ({ page }) => {
    await page.setViewportSize({ width: 1024, height: 768 })
    await page.goto('/agents')

    // Default card visible
    const defaultCard = page.locator('.hidden.sm\\:block').first()
    await expect(defaultCard).toBeVisible()

    // Compact card hidden
    const compactCard = page.locator('.sm\\:hidden').first()
    await expect(compactCard).not.toBeVisible()
  })

  test('transitions smoothly when resizing', async ({ page }) => {
    await page.setViewportSize({ width: 1024, height: 768 })
    await page.goto('/agents')

    // Resize to mobile
    await page.setViewportSize({ width: 375, height: 667 })

    // No layout shift errors
    const cls = await page.evaluate(() => {
      return (window.performance.getEntriesByType('layout-shift') as any[])
        .reduce((sum, entry) => sum + entry.value, 0)
    })
    expect(cls).toBeLessThan(0.1) // CLS < 0.1 is good
  })
})

test.describe('Expanded Card Modal', () => {
  test('opens on card click (desktop)', async ({ page }) => {
    await page.setViewportSize({ width: 1024, height: 768 })
    await page.goto('/agents')

    // Click first agent card
    await page.click('[data-testid="agent-card"]:first-child')

