  test('shows all fields without truncation', async ({ page }) => {
    await page.goto('/agents')
    await page.click('[data-testid="agent-card"]:first-child')

    const modal = page.locator('role=dialog')

    // System prompt visible and full
    const systemPrompt = modal.locator('pre')
