  })

  test('tooltip appears on truncated text hover', async ({ page }) => {
    await page.goto('/agents')

    const title = page.locator('.truncate').first()
    await title.hover()

    const tooltip = page.locator('role=tooltip')
    await expect(tooltip).toBeVisible({ timeout: 500 })
  })

  test('keyboard navigation works', async ({ page }) => {
    await page.goto('/agents')

    // Tab to first card
    await page.keyboard.press('Tab')

    // Space opens expanded view
    await page.keyboard.press('Space')
    const modal = page.locator('role=dialog')
    await expect(modal).toBeVisible()

    // Tab cycles within modal (focus trap)
    await page.keyboard.press('Tab')
    const focusedElement = page.locator(':focus')
    const modalContains = await modal.locator(':focus').count()
    expect(modalContains).toBe(1) // Focus stayed in modal
