// frontend/e2e/performance/cards.spec.ts
test.describe('Performance', () => {
  test('compact card renders in <100ms', async ({ page }) => {
    await page.goto('/agents')

    const metrics = await page.evaluate(() => {
      const entries = performance.getEntriesByType('measure')
      return entries.find(e => e.name === 'CompactCard:render')
    })

    expect(metrics.duration).toBeLessThan(100)
  })

  test('expanded modal opens in <150ms', async ({ page }) => {
    await page.goto('/agents')

    const startTime = Date.now()
    await page.click('[data-testid="agent-card"]:first-child')
    await page.waitForSelector('[role="dialog"]', { state: 'visible' })
    const duration = Date.now() - startTime

    expect(duration).toBeLessThan(150)
  })

  test('no layout shifts (CLS < 0.1)', async ({ page }) => {
    await page.goto('/agents')

    const cls = await page.evaluate(() => {
      return new Promise(resolve => {
        let clsScore = 0
        new PerformanceObserver(list => {
          for (const entry of list.getEntries()) {
            if ((entry as any).hadRecentInput) continue
            clsScore += (entry as any).value
          }
          resolve(clsScore)
        }).observe({ type: 'layout-shift', buffered: true })

        setTimeout(() => resolve(clsScore), 3000)
      })
    })

    expect(cls).toBeLessThan(0.1)
  })
})
```

